mapper.select:
SELECT COUNT(*)
FROM users
WHERE username = #{username}

SELECT COUNT(*)
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
SET email = #{request.email}, name = #{request.name}
WHERE username = #{username}

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM users
    WHERE username = #{username}
)

SELECT EXISTS (
    SELECT 1
    FROM users
    WHERE email = #{email}
)
